'''

Time: O(logn)
'''

def find_single_element(arr):
    n = len(arr)
    low, high = 0, n-1

    while low < high:
        mid = (low + high)//2
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
        if mid % 2 == 1:
            mid -= 1
        
        if arr[mid] == arr[mid+1]:
            low = mid + 2
        else:
            high = mid

    return arr[low]

nums = [1, 1, 2, 2, 3, 3, 4, 5, 5, 6, 6]
res = find_single_element(nums)
print(res)